INSERT INTO autori (nome, cognome, biografia, data_nascita, data_morte) VALUES
('Mark', 'Twain', 'Scrittore e umorista americano, noto per "Le avventure di Tom Sawyer".', '1835-11-30', '1910-04-21'),
('Ernest', 'Hemingway', 'Autore e giornalista, noto per il suo stile sobrio e per opere come "Il vecchio e il mare".', '1899-07-21', '1961-07-02'),
('Edgar Allan', 'Poe', 'Scrittore, poeta e critico letterario americano, famoso per i suoi racconti di mistero.', '1809-01-19', '1849-10-07'),
('Emily', 'Dickinson', 'Poetessa americana nota per il suo stile unico e la sua poesia intensamente personale.', '1830-12-10', '1886-05-15'),
('F. Scott', 'Fitzgerald', 'Autore di "Il grande Gatsby", che esplora temi di decadenza e esuberanza.', '1896-09-24', '1940-12-21'),
('Harper', 'Lee', 'Autrice di "Il buio oltre la siepe", un opera fondamentale sulla giustizia e l ineguaglianza razziale.', '1926-04-28', '2016-02-19'),
('Sylvia', 'Plath', 'Poetessa e scrittrice, nota per "La campana di vetro" e la sua intensa poesia confessionalista.', '1932-10-27', '1963-02-11'),
('Jack', 'London', 'Scrittore e giornalista, noto per "Il richiamo della foresta" e "Zanna Bianca".', '1876-01-12', '1916-11-22'),
('Ralph Waldo', 'Emerson', 'Filosofo e scrittore, leader del movimento trascendentalista americano.', '1803-05-25', '1882-04-27'),
('Walt', 'Whitman', 'Poeta e giornalista, autore di "Foglie d erba", un influenza significativa sulla poesia americana.', '1819-05-31', '1892-03-26'),
('Nathaniel', 'Hawthorne', 'Scrittore noto per i suoi racconti gotici e per il romanzo "La lettera scarlatta".', '1804-07-04', '1864-05-19'),
('Henry', 'James', 'Autore di romanzi e racconti che esplorano la coscienza e la percezione.', '1843-04-15', '1916-02-28'),
('Herman', 'Melville', 'Scrittore di "Moby Dick", una critica complessa alla società e alla natura umana.', '1819-08-01', '1891-09-28'),
('Langston', 'Hughes', 'Poeta, scrittore e attivista sociale, figura chiave della Harlem Renaissance.', '1902-02-01', '1967-05-22'),
('Zora Neale', 'Hurston', 'Scrittrice e antropologa, autrice di "Their Eyes Were Watching God".', '1891-01-07', '1960-01-28'),
('Louisa May', 'Alcott', 'Autrice di "Piccole donne", ha esplorato i temi del femminismo e dell indipendenza.', '1832-11-29', '1888-03-06'),
('John', 'Steinbeck', 'Scrittore di "Furore" e "Uomini e topi", esplora le difficoltà delle classi lavoratrici.', '1902-02-27', '1968-12-20'),
('Ray', 'Bradbury', 'Autore di "Fahrenheit 451", esamina i temi della censura e della perdita della libertà intellettuale.', '1920-08-22', '2012-06-05'),
('Kurt', 'Vonnegut', 'Scrittore di "Mattatoio n. 5", conosciuto per il suo umorismo nero e la critica sociale.', '1922-11-11', '2007-04-11'),
('Tennessee', 'Williams', 'Drammaturgo noto per "Un tram che si chiama Desiderio" e "La gatta sul tetto che scotta".', '1911-03-26', '1983-02-25'),
('Arthur', 'Miller', 'Drammaturgo, autore di "Morte di un commesso viaggiatore", ha esplorato l integrità personale.', '1915-10-17', '2005-02-10'),
('Robert', 'Frost', 'Poeta noto per la sua rappresentazione realistica della vita rurale e il suo comando della lingua americana parlata.', '1874-03-26', '1963-01-29'),
('Eudora', 'Welty', 'Scrittrice noto per i suoi ritratti acuti dei personaggi e della vita nel sud profondo degli USA.', '1909-04-13', '2001-07-23');
INSERT INTO autori (nome, cognome, biografia, data_nascita) VALUES
('Stephen', 'King', 'Scrittore di horror e fantascienza, noto per libri come "It" e "Shining".', '1947-09-21'),
('Don', 'DeLillo', 'Autore americano di romanzi che esplorano la realtà contemporanea, come "Rumore bianco".', '1936-11-20'),
('Joyce', 'Carol Oates', 'Scrittrice prolifica, nota per il suo stile gotico e psicologico in opere come "Zombie".', '1938-06-16'),
('George R. R.', 'Martin', 'Scrittore di fantasy, famoso per "A Song of Ice and Fire", che ha ispirato la serie TV "Game of Thrones".', '1948-09-20'),
('Margaret', 'Atwood', 'Autrice canadese di "Il racconto dell ancella", esplora temi di genere e potere.', '1939-11-18'),
('John', 'Grisham', 'Ex avvocato e politico, noto per i suoi thriller legali come "Il rapporto Pelican".', '1955-02-08'),
('Nora', 'Roberts', 'Autrice prolifica di romanzi rosa e thriller, ha scritto più di 200 libri.', '1950-10-10'),
('Dan', 'Brown', 'Autore di "Il codice da Vinci", noto per i suoi romanzi ricchi di suspense e misteri storici.', '1964-06-22'),
('James', 'Patterson', 'Autore di thriller e fiction per giovani adulti, noto per la serie di Alex Cross.', '1947-03-22'),
('Sandra', 'Cisneros', 'Scrittrice messico-americana, nota per "La casa in Mango Street" e le sue riflessioni sull identità culturale.', '1954-12-20');
INSERT INTO autori (nome, cognome, biografia, data_nascita, data_morte) VALUES
