INSERT INTO libri (isbn, titolo, trama) VALUES
('9780451524935', 'Le avventure di Tom Sawyer', 'Tom Sawyer vive lungo il Mississippi e si imbarca in molte avventure con il suo amico Huck Finn, esplorando caverne e cercando tesori.'),
('9780684801223', 'Il vecchio e il mare', 'Un anziano pescatore cubano lotta con un gigantesco marlin nel Golfo del Messico, una metafora della lotta umana.'),
('9780553213450', 'I racconti del mistero', 'Raccolta di storie brevi che include il Corvo e il Pozzo e il Pendolo, intrise di terrore e mistero gotico.'),
('9781537662455', 'Poesie scelte', 'Una collezione delle poesie più note di Emily Dickinson, che riflettono la sua profondità e sensibilità.'),
('9780743273565', 'Il grande Gatsby', 'La storia di Jay Gatsby e del suo amore non corrisposto per la bella Daisy Buchanan nella New York degli anni Venti.'),
('9780060935467', 'Il buio oltre la siepe', 'Un avvocato del sud difende un uomo nero ingiustamente accusato di stupro, esplorando temi di razzismo e perdita dell innocenza.'),
('9780060837020', 'La campana di vetro', 'La lotta di una giovane donna contro la depressione e la pressione sociale negli Stati Uniti degli anni 50.'),
('9781505294776', 'Il richiamo della foresta', 'Un cane domestico si trasforma in una creatura selvaggia, lottando per la sopravvivenza nel Klondike.'),
('9781449556753', 'Nature', 'Un saggio che pone le basi del trascendentalismo, invitando a una connessione personale e diretta con il mondo naturale.'),
('9781420959719', 'Foglie d erba', 'Un epico poema in lode alla natura, alla democrazia e all individualità, scritto con uno stile libero e fluente.');
INSERT INTO libri (isbn, titolo, trama) VALUES
('9780450411434', 'It', 'La storia di un entità malefica che prende la forma di un pagliaccio e terrorizza i bambini di una piccola città del Maine.'),
('9780143105985', 'Rumore bianco', 'Un professore e la sua famiglia affrontano la paura della morte e il bombardamento mediatico nella società moderna.'),
('9780062796065', 'Zombie', 'Un racconto inquietante e psicologico su un uomo ossessionato dal diventare un serial killer.'),
('9780553801507', 'A Game of Thrones', 'Il primo libro della serie fantasy epica che descrive la lotta tra nobili famiglie per il controllo del Trono di Spade.'),
('9780385490818', 'Il racconto dell ancella', 'Un futuro distopico in cui le donne sono sottomesse e usate solo per la riproduzione in una società totalitaria.'),
('9780385338608', 'Il rapporto Pelican', 'Un giovane avvocato scopre una cospirazione alla Corte Suprema che coinvolge l assassinio di due giudici.'),
('9780515150636', 'Visioni in bianco', 'Una storia di amore e mistero che segue la vita di una giovane artista tra passato e futuro.'),
('9780385504201', 'Il codice da Vinci', 'Un simbologo e una crittografa seguono una serie di indizi collegati a Leonardo da Vinci per risolvere un omicidio.'),
('9780316693646', 'Along Came a Spider', 'Un detective di polizia insegue un geniale serial killer che ha rapito due bambini da una scuola privata.'),
('9780679734772', 'La casa in Mango Street', 'La vita di una ragazza latina che cresce in un quartiere povero di Chicago, raccontata attraverso una serie di vignette poetiche.');
INSERT INTO libri (isbn, titolo, trama) VALUES
('9788807901011', 'La Divina Commedia', 'Un viaggio epico attraverso l Inferno, il Purgatorio e il Paradiso, guidato dal poeta Dante e dal suo mentore Virgilio.'),
('9788804684889', 'Il barone rampante', 'La storia di un ragazzo che decide di vivere sugli alberi e non toccare più terra, esplorando la libertà e l avventura.'),
('9788845292613', 'Il nome della rosa', 'Un monaco indaga su una serie di morti misteriose in un abbazia italiana nel Medioevo.'),
('9788806177694', 'Se questo è un uomo', 'Le riflessioni di Primo Levi sulle sue esperienze nel campo di concentramento di Auschwitz durante la Seconda guerra mondiale.'),
('9788845298325', 'Il Gattopardo', 'La decadenza dell aristocrazia siciliana attraverso gli occhi del principe di Salina durante l unificazione italiana.');
INSERT INTO libri (isbn, titolo, trama) VALUES
('9788806185750', 'Gomorra', 'Un esplorazione profonda e a tratti cruda della Camorra napoletana e del suo impatto sulla società italiana.'),
('9788804667133', 'La lunga vita di Marianna Ucria', 'Il viaggio emotivo e intellettuale di una donna sorda nel Settecento siciliano, in cerca di autonomia e comprensione.');
